sys_script_client.script:
function onLoad() {
//     var getDummyData = new GlideAjax('GetDummyData');
//     getDummyData.addParam('sysparm_name', 'getDummyData');
//     getDummyData.getXML(populateDummyData);

    var ga = new GlideAjax('GetSalesforceToken');
    ga.addParam('sysparm_name', 'getToken');
    ga.getXML(callback);

    function populateDummyData(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        g_form.addInfoMessage(answer);
        answer = JSON.parse(answer);
        g_form.addInfoMessage(answer.var1);
        g_form.addInfoMessage(answer.var2);
    }

    function callback(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var data = JSON.parse(answer);
        g_form.addInfoMessage(data);
        var data = JSON.parse(data);
        g_form.addInfoMessage(data.access_token);
        g_form.addInfoMessage(data.instance_url);
    }

}
