sys_script_include.script:
var GetSalesforceToken = Class.create();
GetSalesforceToken.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getToken: function() {
        gs.info('### Script include: GetSalesforceToken');

        var requestBody;
        var responseBody;
        var status;
        var sm;
        try {
            sm = new sn_ws.RESTMessageV2("RM_Salesforce_Token", "getToken");
            sm.setHttpTimeout(10000);
            response = sm.execute();
            responseBody = response.haveError() ? response.getErrorMessage() : response.getBody();
            status = response.getStatusCode();
        } catch (ex) {
            responseBody = ex.getMessage();
            status = '500';
        } finally {
            requestBody = sm ? sm.getRequestBody() : null;
        }
        gs.info("Response: " + responseBody);
        gs.info("HTTP Status: " + status);
//         var obj = {
//             access_token: responseBody.access_token,
// 			instance_url: responseBody.instance_url
//         };
        var data = JSON.stringify(responseBody);
        return data;
    },
    type: 'GetSalesforceToken'
});
