sysauto_script.condition:
var answer = true;
var rightNow = new GlideDateTime();
var dayOfWeek = rightNow.getDayOfWeekLocalTime();

if (dayOfWeek == 6 || dayOfWeek == 7) {
	answer = false;
}


answer;

sysauto_script.script:
var month = rightNow.getMonthLocalTime();
var day = rightNow.getDayOfMonthLocalTime();

gs.info("Month: " + month);
gs.info("Day: " + day);

var overdueNITasks = GlideRecord('x_690352_needit_needit');
overdueNITasks.addQuery('ude_date', '<=', rightNow);
overdueNITasks.addQuery('state', '!=', 3);
overdueNITasks.query();

while(overdueNITasks.next()) {
	gs.info("Overdue NeedIt record = " + overdueNITasks.number);
}
